import React, {useState} from "react";
import api from "../api/axios";
import { saveLocalRecord } from "../api/syncService";

export default function PatientForm(){
  const [first,setFirst]=useState("");
  const [last,setLast]=useState("");
  const [contact,setContact]=useState("");
  const [msg,setMsg]=useState("");

  const submit = async (e)=>{
    e.preventDefault();
    const payload = {
      first_name:first,
      last_name:last,
      contact
    };
    // attempt to POST to server. If fails -> save locally
    try {
      await api.post("/patients/", payload);
      setMsg("Saved online");
      setFirst(""); setLast(""); setContact("");
    } catch (err) {
      await saveLocalRecord("patient", payload);
      setMsg("Saved locally (offline) — will sync when online");
      setFirst(""); setLast(""); setContact("");
    }
  };

  return (
    <form onSubmit={submit}>
      <input placeholder="First name" value={first} onChange={e=>setFirst(e.target.value)} />
      <input placeholder="Last name" value={last} onChange={e=>setLast(e.target.value)} />
      <input placeholder="Contact" value={contact} onChange={e=>setContact(e.target.value)} />
      <button>Save Patient</button>
      {msg && <p>{msg}</p>}
    </form>
  );
}
